using UnityEngine;
using UnityEngine.SceneManagement;
using UnityEngine.UI;
using TMPro;

public class HatSelection : MonoBehaviour
{
    [Header("UI References")]
    public Button[] hatButtons; // En knapp per hat
    public Button proceedButton;
    public TextMeshProUGUI hatNameText; // Optional: viser valgt hat navn

    [Header("Hat Preview")]
    public GameObject[] hatPrefabs;
    public Transform hatPreviewParent; // Hvor preview-hatten spawnes
    public Camera previewCamera; // Optional: dedikert preview camera
    public RawImage previewImage; // Optional: preview i UI

    private int selectedHatIndex = 0;
    private GameObject currentHatInstance;

    void Start()
    {
        // Setup hat buttons - hver knapp velger en spesifikk hat
        for (int i = 0; i < hatButtons.Length; i++)
        {
            int hatIndex = i; // Capture for closure
            hatButtons[i].onClick.AddListener(() => SelectHat(hatIndex));
        }

        // Setup proceed button
        proceedButton.onClick.AddListener(ProceedToGame);

        // Setup preview camera hvis satt
        if (previewCamera != null && previewImage != null)
        {
            RenderTexture renderTexture = new RenderTexture(512, 512, 16);
            previewCamera.targetTexture = renderTexture;
            previewImage.texture = renderTexture;
        }

        // Vis første hat som default
        SelectHat(0);
    }

    void Update()
    {
        // Roter preview-hatten for bedre visning
        if (currentHatInstance != null)
        {
            currentHatInstance.transform.Rotate(Vector3.up, 50f * Time.deltaTime);
        }
    }

    void SelectHat(int hatIndex)
    {
        if (hatIndex < 0 || hatIndex >= hatPrefabs.Length)
        {
            Debug.LogWarning($"HatSelection: Invalid hat index {hatIndex}");
            return;
        }

        selectedHatIndex = hatIndex;

        // Oppdater visuelt - highlight valgt knapp
        UpdateButtonVisuals();

        // Spawn preview av hatten
        UpdatePreview(hatIndex);

        // Oppdater hat navn text hvis assigned
        if (hatNameText != null)
        {
            hatNameText.text = hatPrefabs[hatIndex].name;
        }

        Debug.Log($"Selected hat: {hatPrefabs[hatIndex].name}");
    }

    void UpdateButtonVisuals()
    {
        for (int i = 0; i < hatButtons.Length; i++)
        {
            Image buttonImage = hatButtons[i].GetComponent<Image>();
            if (buttonImage != null)
            {
                // Highlight valgt knapp med grønn farge
                buttonImage.color = (i == selectedHatIndex) ? new Color(0.3f, 1f, 0.3f) : Color.white;
            }

            // Optional: endre font størrelse på valgt knapp
            TextMeshProUGUI buttonText = hatButtons[i].GetComponentInChildren<TextMeshProUGUI>();
            if (buttonText != null)
            {
                buttonText.fontSize = (i == selectedHatIndex) ? 28 : 24;
            }
        }
    }

    void UpdatePreview(int hatIndex)
    {
        // Fjern gammel preview
        if (currentHatInstance != null)
            Destroy(currentHatInstance);

        // Spawn ny preview
        if (hatPrefabs[hatIndex] != null && hatPreviewParent != null)
        {
            currentHatInstance = Instantiate(hatPrefabs[hatIndex], hatPreviewParent);
            currentHatInstance.transform.localPosition = Vector3.zero;
            currentHatInstance.transform.localRotation = Quaternion.identity;

            // Fjern physics/colliders fra preview
            Collider[] colliders = currentHatInstance.GetComponentsInChildren<Collider>();
            foreach (var col in colliders)
                col.enabled = false;

            Rigidbody[] rigidbodies = currentHatInstance.GetComponentsInChildren<Rigidbody>();
            foreach (var rb in rigidbodies)
                Destroy(rb);
        }
    }

    void ProceedToGame()
    {
        // Lagre valgt hat i GameManager
        if (GameManager.Instance != null)
        {
            GameManager.Instance.SelectedHatIndex = selectedHatIndex;
        }

        // Backup i PlayerPrefs
        PlayerPrefs.SetInt("SelectedHat", selectedHatIndex);
        PlayerPrefs.Save();

        Debug.Log($"Starting game with difficulty: {GameManager.Instance?.SelectedDifficulty}, hat: {hatPrefabs[selectedHatIndex].name}");

        // Start spillet
        SceneManager.LoadScene("SampleScene");
    }

    void OnDestroy()
    {
        // Cleanup preview
        if (currentHatInstance != null)
            Destroy(currentHatInstance);
    }
}
